<?phprequire $_SERVER['DOCUMENT_ROOT'] . '/Core.php';require $_SERVER['DOCUMENT_ROOT'] . '/lib/Pusher/Pusher.php';$kun = new System;$user = $kun->user();$file = fopen("log.txt",'a+') or die("lỗi");$info= "trans_id : ".$_GET['request_id']." | status: ".$_GET['status']." | time: ".date('d-m-Y H:i:s')."\n________________________________________________";fwrite($file,"".$info." \r\n\n");fclose($file);if ($_GET['status']) {	$code = $_GET['status'];	$tranid = $_GET['request_id'];	$value_receive = $_GET['amount_recieve'];	$get = mysqli_fetch_assoc(mysqli_query($kun->connect_db(), "SELECT * FROM `napthe` WHERE `tranid` = '" . $tranid . "'  "));	$money = $get['amount']*110/100;	if($get){		if ($code == 'success') {			// update tiền			mysqli_query($kun->connect_db(), "UPDATE `users` SET `money` = `money` + '" . $money . "' WHERE `username` = '" . $get['username'] . "' ");			// update tiền nạp			mysqli_query($kun->connect_db(), "UPDATE `users` SET `money_nap` = `money_nap` + '" . $money . "' WHERE `username` = '" . $get['username'] . "' ");			// update trạng thái thẻ thành công			mysqli_query($kun->connect_db(), "UPDATE `napthe` SET `status` = '1' WHERE `tranid`='" . $tranid . "'");			// Update vào lịch sử user			mysqli_query($kun->connect_db(), "INSERT INTO `user_history_system` (`username`, `action`, `action_name`, `sotien`, `mota`, `time`) VALUES ('" . $get['username'] . "', 'Nạp Thẻ', 'Nạp Thẻ Tự Động', '+" . number_format($money) . "đ', 'Nạp Thẻ " . $get['type'] . " Thành Công!', '" . time() . "')");			} else if ($code == 'wrong_amount') {			// update trạng thái thẻ sai mệnh giá			mysqli_query($kun->connect_db(), "UPDATE `napthe` SET `status` = '0' WHERE `tranid`='" . $tranid . "'");					} else  {			// update trạng thái thẻ thất bại			mysqli_query($kun->connect_db(), "UPDATE `napthe` SET `status` = '0' WHERE `tranid`='" . $tranid . "'");		}		echo json_encode(array(			'status' => 1,			'messagge' => 'Đã nhận callback'		));	}	else{		echo json_encode(array(			'status' => 200,			'messagge' => 'Không thể thay đổi trạng thái thẻ'		));	}		}